import * as React from 'react';
import * as PropTypes from 'prop-types';
export interface ResponsiveWrapperProps {
    children: (props: {
        width: number;
        height: number;
    }) => React.ReactNode;
}
export interface ResponsiveWrapperState {
    dimensions: {
        width: number;
        height: number;
    };
}
export default class ResponsiveWrapper extends React.Component<ResponsiveWrapperProps, ResponsiveWrapperState> {
    static propTypes: {
        children: PropTypes.Validator<(...args: any[]) => any>;
    };
    state: {
        dimensions: {
            width: number;
            height: number;
        };
    };
    render(): JSX.Element;
}
