/**
 * Decompose a color for use with react-motion.
 *
 * @param {string} _color
 * @param {Object} [_config]
 * @return {Object}
 */
export declare const colorMotionSpring: (_color: any, _config: any) => {
    colorR: any;
    colorG: any;
    colorB: any;
};
/**
 * Re-assemble interpolated color components for easy use.
 *
 * @param {number} colorR
 * @param {number} colorG
 * @param {number} colorB
 * @return {string}
 */
export declare const getInterpolatedColor: ({ colorR, colorG, colorB }: {
    colorR: any;
    colorG: any;
    colorB: any;
}) => string;
